mapper.update:
UPDATE TB_BASIC_BOARD
   SET CONTENT = #{content}
 WHERE STORY_NO = #{story_no}

mapper.insert:
INSERT INTO TB_BASIC_BOARD_COMMENT
    (
     story_no
     , comment_no
     , content
     , init_date
     , writer
     )
VALUES
    (
     #{story_no}
     , 'COM' || TO_CHAR(SYSDATE, 'YYYYMMDDHH24MI') || COMMENT_NO_SEQ.NEXTVAL
     , #{comment_content}
     , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI')
    , #{comment_writer}
     )

mapper.update:
UPDATE TB_BASIC_BOARD
SET VIEWS = VIEWS + 1
WHERE STORY_NO = #{story_no}
